// ald_system_provider.dart

import 'dart:async';
import 'package:flutter/foundation.dart';
import '../models/system_component.dart';
import '../models/data_point.dart';
import '../models/system_log_entry.dart';
import '../services/ald_system_simulation_service.dart';

class SystemStateProvider with ChangeNotifier {
  // Map to store all system components by their names
  final Map<String, SystemComponent> _components = {};

  // System status
  bool isSystemRunning = false;

  // System log
  final List<SystemLogEntry> systemLog = [];

  // Simulation Service
  late final AldSystemSimulationService simulationService;

  // StreamSubscription for simulation
  StreamSubscription<void>? _simulationSubscription;

  // Constructor
  SystemStateProvider() {
    _initializeComponents();
    simulationService = AldSystemSimulationService(systemStateProvider: this);
    startSimulation();
  }

  // Public getter to access components
  Map<String, SystemComponent> get components => _components;

  // Initialize all components and populate the _components map
  void _initializeComponents() {
    // Define all system components
    final componentsList = [
      SystemComponent(
        name: 'Nitrogen Generator',
        description: 'Provides inert gas for purging and as a carrier gas',
        currentValues: {'flow_rate': 0.0, 'purity': 99.9},
        setValues: {'flow_rate': 0.0},
      ),
      SystemComponent(
        name: 'MFC',
        description: 'Mass Flow Controller for precise gas flow regulation',
        currentValues: {'flow_rate': 0.0, 'pressure': 1.0, 'percent_correction': 0.0},
        setValues: {'flow_rate': 0.0},
      ),
      SystemComponent(
        name: 'Backline Heater',
        description: 'Heats precursor delivery lines to prevent condensation',
        currentValues: {'temperature': 25.0},
        setValues: {'temperature': 25.0},
      ),
      SystemComponent(
        name: 'Frontline Heater',
        description: 'Heats gas lines near the reaction chamber',
        currentValues: {'temperature': 25.0},
        setValues: {'temperature': 25.0},
      ),
      SystemComponent(
        name: 'Precursor Heater 1',
        description: 'Heats precursor container to maintain vapor pressure',
        currentValues: {'temperature': 25.0, 'fill_level': 100.0},
        setValues: {'temperature': 25.0},
      ),
      SystemComponent(
        name: 'Precursor Heater 2',
        description: 'Heats precursor container to maintain vapor pressure',
        currentValues: {'temperature': 25.0, 'fill_level': 100.0},
        setValues: {'temperature': 25.0},
      ),
      SystemComponent(
        name: 'Reaction Chamber',
        description: 'Where the ALD reactions occur on the substrate',
        currentValues: {'temperature': 25.0, 'pressure': 1.0},
        setValues: {'temperature': 25.0, 'pressure': 1.0},
      ),
      SystemComponent(
        name: 'Valve 1',
        description: 'Controls the flow of precursor 1 and purge gas',
        currentValues: {'status': 0.0}, // 0.0 for closed, 1.0 for open
        setValues: {'status': 0.0},
      ),
      SystemComponent(
        name: 'Valve 2',
        description: 'Controls the flow of precursor 2 and purge gas',
        currentValues: {'status': 0.0},
        setValues: {'status': 0.0},
      ),
      SystemComponent(
        name: 'Pressure Control System',
        description: 'Maintains optimal pressure in the reaction chamber',
        currentValues: {'pressure': 1.0},
        setValues: {'pressure': 1.0},
      ),
      SystemComponent(
        name: 'Vacuum Pump',
        description: 'Evacuates the chamber between precursor pulses',
        currentValues: {'power': 100.0},
        setValues: {'power': 100.0},
      ),
      // Add any other components as needed
    ];

    // Populate the _components map
    for (var component in componentsList) {
      _components[component.name] = component;
    }

    _addLogEntry('System initialized', ComponentStatus.normal);
  }

  // Method to activate a component
  void activateComponent(String name) {
    final component = getComponentByName(name);
    component.isActivated = true;
    _addLogEntry('${component.name} activated', ComponentStatus.normal);
    notifyListeners();
  }

  // Method to deactivate a component
  void deactivateComponent(String name) {
    final component = getComponentByName(name);
    component.isActivated = false;
    _addLogEntry('${component.name} deactivated', ComponentStatus.normal);
    notifyListeners();
  }

  // Method to set a component's set value
  void setComponentSetValue(String name, String parameter, double value) {
    final component = getComponentByName(name);
    if (component.setValues.containsKey(parameter)) {
      component.setValues[parameter] = value;
      _addLogEntry('${component.name} set $parameter to $value', ComponentStatus.normal);
      notifyListeners();
    } else {
      _addLogEntry('Attempted to set unknown parameter "$parameter" for ${component.name}', ComponentStatus.warning);
    }
  }

  // Method to start the system
  void startSystem() {
    if (!canStartSystem()) {
      _addLogEntry('System cannot start. Please activate and configure all required components.', ComponentStatus.error);
      return;
    }
    isSystemRunning = true;
    _addLogEntry('System started', ComponentStatus.normal);
    simulationService.startSimulation();
    notifyListeners();
  }

  // Method to pause the system
  void pauseSystem() {
    isSystemRunning = false;
    _addLogEntry('System paused', ComponentStatus.normal);
    simulationService.stopSimulation();
    notifyListeners();
  }

  // Check if the system can start
  bool canStartSystem() {
    // List of components that must be activated and configured
    List<SystemComponent> requiredComponents = [
      _components['Nitrogen Generator']!,
      _components['MFC']!,
      _components['Backline Heater']!,
      _components['Frontline Heater']!,
      _components['Precursor Heater 1']!,
      _components['Precursor Heater 2']!,
      _components['Reaction Chamber']!,
      _components['Pressure Control System']!,
      _components['Vacuum Pump']!,
      // Add Precursor Heater 3 if applicable
    ];

    for (var component in requiredComponents) {
      if (!component.isActivated) {
        _addLogEntry('${component.name} is not activated', ComponentStatus.warning);
        return false;
      }
      // Check if all required set values are configured
      for (var key in component.setValues.keys) {
        if (component.setValues[key] == null) {
          _addLogEntry('${component.name} set value for $key is not configured', ComponentStatus.warning);
          return false;
        }
      }
    }
    return true;
  }

  // Method to run diagnostics on a component
  void runDiagnostic(String componentName) {
    final component = getComponentByName(componentName);
    _addLogEntry('Running diagnostic for ${component.name}', ComponentStatus.normal);
    // Simulate diagnostic result
    Future.delayed(Duration(seconds: 2), () {
      _addLogEntry('${component.name} diagnostic completed: All systems nominal', ComponentStatus.normal);
      notifyListeners();
    });
  }

  // Method to add a log entry
  void _addLogEntry(String message, ComponentStatus severity) {
    systemLog.add(SystemLogEntry(
      timestamp: DateTime.now(),
      message: message,
      severity: severity,
    ));
    // Keep only the last 100 log entries
    if (systemLog.length > 100) {
      systemLog.removeAt(0);
    }
    notifyListeners();
  }

  // Getter to retrieve recent logs
  List<SystemLogEntry> getRecentLogs({int count = 10}) {
    return systemLog.reversed.take(count).toList();
  }

  // Method to retrieve a component by name
  SystemComponent getComponentByName(String name) {
    if (_components.containsKey(name)) {
      return _components[name]!;
    }
    throw ArgumentError('Unknown component name: $name');
  }

  // Start continuous simulation
  void startSimulation() {
    _simulationSubscription = simulationService.startContinuousSimulation().listen((_) {
      notifyListeners();
    });
  }

  // Stop simulation
  void stopSimulation() {
    _simulationSubscription?.cancel();
  }

  // Control methods for specific components (from SystemStateProvider)
  void toggleComponentActivation(String componentName) {
    final component = getComponentByName(componentName);
    if (component.isActivated) {
      deactivateComponent(componentName);
    } else {
      activateComponent(componentName);
    }
  }

  void setComponentSetpoint(String componentName, String parameter, double setpoint) {
    setComponentSetValue(componentName, parameter, setpoint);
  }

  // Handle recipe steps (from SystemStateProvider)
  void handleRecipeStep(Map<String, dynamic> parameters) {
    parameters.forEach((key, value) {
      switch (key) {
        case 'v1':
          setComponentSetValue('Valve 1', 'status', value ? 1.0 : 0.0);
          break;
        case 'v2':
          setComponentSetValue('Valve 2', 'status', value ? 1.0 : 0.0);
          break;
        case 'purge_valve':
          if (value == true) {
            setComponentSetValue('Valve 1', 'status', 0.0);
            setComponentSetValue('Valve 2', 'status', 0.0);
          }
          break;
        case 'frontline_heater':
          setComponentSetValue('Frontline Heater', 'temperature', value);
          break;
        case 'backline_heater':
          setComponentSetValue('Backline Heater', 'temperature', value);
          break;
      // Add more cases as needed for other components
      }
    });
    notifyListeners();
  }

  // Emergency stop method
  void emergencyStop() {
    // Deactivate critical components
    deactivateComponent('Nitrogen Generator');
    setComponentSetValue('MFC', 'flow_rate', 0.0);
    deactivateComponent('Frontline Heater');
    deactivateComponent('Backline Heater');
    deactivateComponent('Vacuum Pump');
    setComponentSetValue('Valve 1', 'status', 0.0);
    setComponentSetValue('Valve 2', 'status', 0.0);
    deactivateComponent('Precursor Heater 1');
    deactivateComponent('Precursor Heater 2');
    _addLogEntry('Emergency stop activated', ComponentStatus.error);
    notifyListeners();
  }

  @override
  void dispose() {
    stopSimulation();
    super.dispose();
  }

  // Methods to check if parameters are within normal ranges
  bool isReactorPressureNormal() {
    final pressure = _components['Reaction Chamber']?.currentValues['pressure'] ?? 0.0;
    return pressure >= 0.9 && pressure <= 1.1;
  }

  bool isReactorTemperatureNormal() {
    final temperature = _components['Reaction Chamber']?.currentValues['temperature'] ?? 0.0;
    return temperature >= 145 && temperature <= 155;
  }

  bool isPrecursorTemperatureNormal(String precursor) {
    final component = _components[precursor];
    if (component != null) {
      final temperature = component.currentValues['temperature'] ?? 0.0;
      return temperature >= 28 && temperature <= 32;
    }
    return false;
  }
}
