import 'package:flutter/material.dart';
import 'package:provider/provider.dart';
import 'screens/system_overview_screen.dart';
import 'providers/diagram_ald_system_provider.dart';
import 'services/navigation_service.dart'; // Import the navigation service

void main() {
  runApp(
    MultiProvider(
      providers: [
        ChangeNotifierProvider(create: (_) => SystemStateProvider()),
        Provider(create: (context) => NavigationService()), // Provide NavigationService
      ],
      child: const ALDMachineApp(),
    ),
  );
}

class ALDMachineApp extends StatelessWidget {
  const ALDMachineApp({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    final navigationService = Provider.of<NavigationService>(context, listen: false);

    return MaterialApp(
      title: 'ALD Machine Control',
      navigatorKey: navigationService.navigatorKey, // Set navigator key
      theme: ThemeData.dark().copyWith(
        primaryColor: Colors.blue,
        scaffoldBackgroundColor: Colors.grey[900],
        appBarTheme: AppBarTheme(
          backgroundColor: Colors.grey[850],
          elevation: 0,
        ),
        cardTheme: CardTheme(
          color: Colors.grey[800],
          elevation: 4,
        ),
        textTheme: const TextTheme(
          bodyLarge: TextStyle(color: Colors.white),
          bodyMedium: TextStyle(color: Colors.white70),
        ),
      ),
      home: const SystemOverviewScreen(),
    );
  }
}
