// system_overview_screen.dart

import 'package:flutter/material.dart';
import 'package:flutter/services.dart';
import 'package:provider/provider.dart';
import '../providers/diagram_ald_system_provider.dart';
import '../widgets/system_diagram_view.dart';
import '../widgets/component_control_overlay.dart';
import '../widgets/graph_overlay.dart';
import '../widgets/troubleshooting_overlay.dart';

class SystemOverviewScreen extends StatefulWidget {
  const SystemOverviewScreen({Key? key}) : super(key: key);

  @override
  _SystemOverviewScreenState createState() => _SystemOverviewScreenState();
}

class _SystemOverviewScreenState extends State<SystemOverviewScreen> {
  final PageController _pageController = PageController();

  @override
  void initState() {
    super.initState();
    // Lock orientation to landscape for the entire screen
    SystemChrome.setPreferredOrientations([
      DeviceOrientation.landscapeLeft,
      DeviceOrientation.landscapeRight,
    ]);
  }

  @override
  void dispose() {
    // Reset orientation when disposing
    SystemChrome.setPreferredOrientations(DeviceOrientation.values);
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('ALD System Overview'),
        actions: [
          Consumer<SystemStateProvider>(
            builder: (context, provider, child) {
              return Row(
                children: [
                  Text(
                    provider.isSystemRunning ? 'Running' : 'Stopped',
                    style: TextStyle(
                      color: provider.isSystemRunning ? Colors.green : Colors.red,
                      fontWeight: FontWeight.bold,
                    ),
                  ),
                  Switch(
                    value: provider.isSystemRunning,
                    onChanged: (value) {
                      if (value) {
                        provider.startSystem();
                      } else {
                        provider.pauseSystem();
                      }
                    },
                    activeColor: Colors.green,
                    inactiveThumbColor: Colors.red,
                  ),
                ],
              );
            },
          ),
        ],
      ),
      body: PageView(
        controller: _pageController,
        scrollDirection: Axis.vertical,
        children: [
          SystemDiagramView(
            overlay: ComponentControlOverlay(),
          ),
          SystemDiagramView(
            overlay: GraphOverlay(),
          ),
          SystemDiagramView(
            overlay: TroubleshootingOverlay(),
          ),
        ],
      ),
    );
  }
}
