// data_simulation_service.dart

import 'dart:async';
import 'dart:math';
import 'package:flutter/foundation.dart';
import '../providers/diagram_ald_system_provider.dart';
import '../models/data_point.dart';

class DataSimulationService {
  final SystemStateProvider diagramSystemStateProvider;
  Timer? _timer;
  final Random _random = Random();

  DataSimulationService({required this.diagramSystemStateProvider});

  // Start the data simulation
  void startSimulation() {
    _timer = Timer.periodic(Duration(seconds: 1), (_) => _simulateData());
  }

  // Stop the data simulation
  void stopSimulation() {
    _timer?.cancel();
  }

  // Simulate data for each activated component
  void _simulateData() {
    for (var component in diagramSystemStateProvider.components.values) {
      if (component.isActivated) {
        component.currentValues.forEach((parameter, value) {
          double newValue = _generateNewValue(parameter, value);
          component.currentValues[parameter] = newValue;
          component.parameterHistory[parameter]?.add(
            DataPoint(timestamp: DateTime.now(), value: newValue),
          );

          // Keep only the last 100 data points
          if (component.parameterHistory[parameter]!.length > 100) {
            component.parameterHistory[parameter]!.removeAt(0);
          }

          // Debug print
          if (kDebugMode) {
            print('Data Point Added - Component: ${component.name}, Parameter: $parameter, Value: $newValue');
          }
        });
      }
    }

    diagramSystemStateProvider.notifyListeners();
  }

  // Generate a new value based on the parameter type
  double _generateNewValue(String parameter, double currentValue) {
    // Define realistic simulation logic based on parameter
    // For simplicity, we'll use a random walk approach

    double delta = (_random.nextDouble() - 0.5) * 0.5; // Random change between -0.25 and +0.25

    switch (parameter) {
      case 'flow_rate':
        return (currentValue + delta).clamp(0.0, 10.0); // Example: 0 to 10 SLPM
      case 'purity':
        return (currentValue + delta).clamp(95.0, 100.0); // Example: 95% to 100%
      case 'pressure':
        return (currentValue + delta).clamp(0.8, 1.2); // Example: 0.8 atm to 1.2 atm
      case 'percent_correction':
        return (currentValue + delta).clamp(-5.0, 5.0); // Example: -5% to +5%
      case 'temperature':
        return (currentValue + delta).clamp(20.0, 300.0); // Example: 20°C to 300°C
      case 'fill_level':
        return (currentValue + delta).clamp(0.0, 100.0); // Example: 0% to 100%
      case 'status':
      // For status, randomly turn on or off
        return _random.nextBool() ? 1.0 : 0.0;
      case 'power':
        return (currentValue + delta).clamp(50.0, 150.0); // Example: 50W to 150W
      default:
        return currentValue;
    }
  }
}
