// component_control_dialog.dart

import 'package:flutter/material.dart';
import 'package:provider/provider.dart';
import '../providers/diagram_ald_system_provider.dart';

class ComponentControlDialog extends StatefulWidget {
  final SystemComponent component;

  ComponentControlDialog({required this.component});

  @override
  _ComponentControlDialogState createState() => _ComponentControlDialogState();
}

class _ComponentControlDialogState extends State<ComponentControlDialog> {
  late Map<String, TextEditingController> _controllers;
  bool _isActivated = false;

  @override
  void initState() {
    super.initState();
    _isActivated = widget.component.isActivated;
    _controllers = {
      for (var key in widget.component.setValues.keys)
        key: TextEditingController(text: widget.component.setValues[key]?.toString() ?? ''),
    };
  }

  @override
  Widget build(BuildContext context) {
    final systemStateProvider = Provider.of<SystemStateProvider>(context, listen: false);

    return AlertDialog(
      title: Text('Configure ${widget.component.name}'),
      content: SingleChildScrollView(
        child: Column(
          children: [
            SwitchListTile(
              title: Text('Activate Component'),
              value: _isActivated,
              onChanged: (value) {
                setState(() {
                  _isActivated = value;
                });
              },
            ),
            ...widget.component.setValues.keys.map((parameter) {
              return TextField(
                controller: _controllers[parameter],
                decoration: InputDecoration(labelText: 'Set ${parameter}'),
                keyboardType: TextInputType.number,
              );
            }).toList(),
          ],
        ),
      ),
      actions: [
        TextButton(
          onPressed: () {
            // Update component activation
            if (_isActivated) {
              systemStateProvider.activateComponent(widget.component.name);
            } else {
              systemStateProvider.deactivateComponent(widget.component.name);
            }

            // Update set values
            widget.component.setValues.keys.forEach((parameter) {
              final value = double.tryParse(_controllers[parameter]!.text);
              if (value != null) {
                systemStateProvider.setComponentSetValue(widget.component.name, parameter, value);
              }
            });

            Navigator.of(context).pop();
          },
          child: Text('Save'),
        ),
      ],
    );
  }
}
