// component_control_overlay.dart

import 'dart:convert';
import 'package:flutter/material.dart';
import 'package:provider/provider.dart';
import 'package:shared_preferences/shared_preferences.dart';
import '../providers/diagram_ald_system_provider.dart';
import 'component_control_dialog.dart';

class ComponentControlOverlay extends StatefulWidget {
  @override
  _ComponentControlOverlayState createState() => _ComponentControlOverlayState();
}

class _ComponentControlOverlayState extends State<ComponentControlOverlay> {
  Map<String, Offset> _componentPositions = {};
  Size _svgSize = Size.zero;

  @override
  void initState() {
    super.initState();
    _loadComponentPositions();
    WidgetsBinding.instance.addPostFrameCallback((_) {
      _updateSvgSize();
    });
  }

  void _updateSvgSize() {
    final RenderBox? renderBox = context.findRenderObject() as RenderBox?;
    if (renderBox != null) {
      setState(() {
        _svgSize = renderBox.size;
      });
    }
  }

  Future<void> _resetComponentPositions() async {
    final prefs = await SharedPreferences.getInstance();
    await prefs.remove('component_positions_control_overlay');
    _initializeDefaultPositions();
    setState(() {}); // Refresh the UI
  }



  void _initializeDefaultPositions() {
    setState(() {
      final fixedPosition = Offset(_svgSize.width * 0.8, _svgSize.height * 0.8);
      _componentPositions = {
        'Nitrogen Generator': fixedPosition,
        'MFC': fixedPosition,
        'Backline Heater': fixedPosition,
        'Frontline Heater': fixedPosition,
        'Precursor Heater 1': fixedPosition,
        'Precursor Heater 2': fixedPosition,
        'Reaction Chamber': fixedPosition,
        'Valve 1': fixedPosition,
        'Valve 2': fixedPosition,
        'Pressure Control System': fixedPosition,
        'Vacuum Pump': fixedPosition, // Ensure this line is included
      };
    });
  }


  Future<void> _loadComponentPositions() async {
    final prefs = await SharedPreferences.getInstance();
    final positionsJson = prefs.getString('component_positions_control_overlay');

    // Initialize default positions
    Map<String, Offset> defaultPositions = {};
    _initializeDefaultPositions();
    defaultPositions = Map<String, Offset>.from(_componentPositions);

    if (positionsJson != null) {
      final positionsMap = jsonDecode(positionsJson) as Map<String, dynamic>;
      setState(() {
        _componentPositions = positionsMap.map((key, value) {
          final offsetList = (value as List<dynamic>).cast<double>();
          return MapEntry(key, Offset(offsetList[0], offsetList[1]));
        });
        // Ensure all components are included
        defaultPositions.forEach((key, value) {
          if (!_componentPositions.containsKey(key)) {
            _componentPositions[key] = value;
          }
        });
      });
    } else {
      // If no saved positions, use default positions
      setState(() {
        _componentPositions = defaultPositions;
      });
    }
  }


  Future<void> _saveComponentPositions() async {
    final prefs = await SharedPreferences.getInstance();
    final positionsMap = _componentPositions.map((key, value) {
      return MapEntry(key, [value.dx, value.dy]);
    });
    await prefs.setString('component_positions_control_overlay', jsonEncode(positionsMap));
  }

  @override
  Widget build(BuildContext context) {
    return Consumer<SystemStateProvider>(
      builder: (context, systemStateProvider, child) {
        return Stack(
          children: [
            ..._componentPositions.entries.map((entry) {
              final componentName = entry.key;
              final componentPosition = entry.value;

              // Get the component by name from the provider
              print('Processing component:  $componentName');

              final component = systemStateProvider.getComponentByName(componentName);
if (component == null) return SizedBox.shrink();

              // After retrieving the component
              print('Retrieved component: ${component.name}');

              return Positioned(
                left: componentPosition.dx,
                top: componentPosition.dy,
                child: GestureDetector(
                  onPanUpdate: (details) {
                    setState(() {
                      _componentPositions[componentName] = Offset(
                        componentPosition.dx + details.delta.dx,
                        componentPosition.dy + details.delta.dy,
                      );
                    });
                  },
                  onPanEnd: (_) {
                    _saveComponentPositions();
                  },
                  onTap: () => _showComponentControlDialog(context, component),
                  child: Container(
                    width: 40, // Touch area size
                    height: 40,
                    alignment: Alignment.center,
                    child: Container(
                      width: 10, // Smaller visual dot size
                      height: 10,
                      decoration: BoxDecoration(
                        color: component.isActivated ? _getStatusColor(component.status) : Colors.grey,
                        shape: BoxShape.circle,
                        border: Border.all(color: Colors.white, width: 1),
                      ),
                    ),
                  ),
                ),
              );
            }).toList(),
          ],
        );
      },
    );
  }

  void _showComponentControlDialog(BuildContext context, SystemComponent component) {
    showDialog(
      context: context,
      builder: (context) => ComponentControlDialog(component: component),
    );
  }

  Color _getStatusColor(ComponentStatus status) {
    switch (status) {
      case ComponentStatus.normal:
        return Colors.green;
      case ComponentStatus.warning:
        return Colors.yellow;
      case ComponentStatus.error:
        return Colors.red;
      default:
        return Colors.grey;
    }
  }
}
