import 'dart:convert';
import 'package:fl_chart/fl_chart.dart';
import 'package:flutter/material.dart';
import 'package:provider/provider.dart';
import 'package:shared_preferences/shared_preferences.dart';
import '../providers/diagram_ald_system_provider.dart';
import '../models/data_point.dart';

class GraphOverlay extends StatefulWidget {
  @override
  _GraphOverlayState createState() => _GraphOverlayState();
}

class _GraphOverlayState extends State<GraphOverlay> {
  // Default component positions
  final Map<String, Offset> _defaultComponentRelativePositions = {
    'Nitrogen Generator': Offset(0.05, 0.80),
    'MFC': Offset(0.20, 0.70),
    'Backline Heater': Offset(0.35, 0.60),
    'Frontline Heater': Offset(0.50, 0.50),
    'Precursor Heater 1': Offset(0.65, 0.40),
    'Precursor Heater 2': Offset(0.80, 0.30),
    'Reaction Chamber': Offset(0.50, 0.20),
    'Pressure Control System': Offset(0.90, 0.85),
    'Vacuum Pump': Offset(0.75, 0.75),
  };

  // Current component positions (loaded from preferences or default)
  late Map<String, Offset> _currentPositions;

  @override
  void initState() {
    super.initState();
    _currentPositions = Map.from(_defaultComponentRelativePositions);
    _loadComponentPositions(); // Load saved positions from SharedPreferences
  }

  // Load positions from SharedPreferences
  Future<void> _loadComponentPositions() async {
    final prefs = await SharedPreferences.getInstance();
    final savedPositions = prefs.getString('component_graph_positions');
    if (savedPositions != null) {
      final positionsMap = jsonDecode(savedPositions) as Map<String, dynamic>;
      setState(() {
        _currentPositions = positionsMap.map((key, value) {
          final offsetList = (value as List).cast<double>();
          return MapEntry(key, Offset(offsetList[0], offsetList[1]));
        });
      });
    }
  }

  // Save positions to SharedPreferences
  Future<void> _saveComponentPositions() async {
    final prefs = await SharedPreferences.getInstance();
    final positionsMap = _currentPositions.map((key, value) => MapEntry(key, [value.dx, value.dy]));
    await prefs.setString('component_graph_positions', jsonEncode(positionsMap));
    print("Component positions saved: $_currentPositions");
  }

  @override
  Widget build(BuildContext context) {
    return LayoutBuilder(
      builder: (context, constraints) {
        final double imageWidth = constraints.maxWidth;
        final double imageHeight = constraints.maxHeight;

        return Consumer<SystemStateProvider>(
          builder: (context, systemStateProvider, child) {
            return Stack(
              children: _currentPositions.entries.map((entry) {
                final componentName = entry.key;
                final relativePosition = entry.value;

                // Calculate absolute position based on relative percentages
                final double left = relativePosition.dx * imageWidth;
                final double top = relativePosition.dy * imageHeight;

                // Get the component by name from the provider
                final component = systemStateProvider.getComponentByName(componentName);
if (component == null) return SizedBox.shrink();

                // Determine which parameter to plot
                final parameterToPlot = _getParameterToPlot(component);

                if (parameterToPlot == null) {
                  return SizedBox.shrink();
                }

                return Positioned(
                  left: left - 50, // Adjust to center the graph
                  top: top - 40,
                  child: GestureDetector(
                    onPanUpdate: (details) {
                      setState(() {
                        // Update position while dragging
                        _currentPositions[componentName] = Offset(
                          (left + details.delta.dx) / imageWidth, // Update relative X
                          (top + details.delta.dy) / imageHeight, // Update relative Y
                        );
                      });
                    },
                    onPanEnd: (_) {
                      // Save positions when dragging ends
                      _saveComponentPositions();
                    },
                    child: Container(
                      width: 100,
                      height: 80,
                      decoration: BoxDecoration(
                        color: Colors.black54,
                        borderRadius: BorderRadius.circular(8),
                      ),
                      padding: EdgeInsets.all(4),
                      child: Column(
                        children: [
                          Text(
                            '$componentName\n($parameterToPlot)',
                            style: TextStyle(color: Colors.white, fontSize: 10),
                            textAlign: TextAlign.center,
                          ),
                          SizedBox(height: 4),
                          Expanded(
                            child: _buildGraph(component, parameterToPlot),
                          ),
                        ],
                      ),
                    ),
                  ),
                );
              }).toList(),
            );
          },
        );
      },
    );
  }

  String? _getParameterToPlot(SystemComponent component) {
    switch (component.name) {
      case 'Nitrogen Generator':
        return 'flow_rate';
      case 'MFC':
        return 'flow_rate';
      case 'Backline Heater':
      case 'Frontline Heater':
      case 'Precursor Heater 1':
      case 'Precursor Heater 2':
      case 'Precursor Heater 3':
        return 'temperature';
      case 'Reaction Chamber':
      case 'Pressure Control System':
        return 'pressure';
      case 'Vacuum Pump':
        return 'power'; // Ensure this matches your data
      default:
        return null;
    }
  }

  Widget _buildGraph(SystemComponent component, String parameter) {
    final dataPoints = component.parameterHistory[parameter];

    if (dataPoints == null || dataPoints.isEmpty) {
      return Container(
        color: Colors.black26,
        child: Center(
          child: Text(
            'No data',
            style: TextStyle(color: Colors.white, fontSize: 8),
          ),
        ),
      );
    }

    // Convert data points to spots
    final firstTimestamp = dataPoints.first.timestamp.millisecondsSinceEpoch.toDouble();
    List<FlSpot> spots = dataPoints.map((dp) {
      double x = (dp.timestamp.millisecondsSinceEpoch.toDouble() - firstTimestamp) / 1000; // in seconds
      double y = dp.value;
      return FlSpot(x, y);
    }).toList();

    // Determine Y-axis range
    double minY = dataPoints.map((dp) => dp.value).reduce((a, b) => a < b ? a : b) - 1;
    double maxY = dataPoints.map((dp) => dp.value).reduce((a, b) => a > b ? a : b) + 1;

    return LineChart(
      LineChartData(
        minX: 0,
        maxX: spots.isNotEmpty ? spots.last.x : 6,
        minY: minY,
        maxY: maxY,
        lineBarsData: [
          LineChartBarData(
            spots: spots,
            isCurved: true,
            color: Colors.blueAccent,
            barWidth: 2,
            dotData: FlDotData(show: false),
          ),
        ],
        titlesData: FlTitlesData(
          show: false,
        ),
        gridData: FlGridData(show: false),
        borderData: FlBorderData(show: false),
      ),
    );
  }
}
