import 'package:flutter/material.dart';
import 'package:provider/provider.dart';
import 'package:fl_chart/fl_chart.dart';
import '../providers/diagram_ald_system_provider.dart';

class GraphView extends StatefulWidget {
  const GraphView({Key? key}) : super(key: key);

  @override
  _GraphViewState createState() => _GraphViewState();
}

class _GraphViewState extends State<GraphView> {
  List<String> selectedComponents = [];
  List<String> selectedParameters = [];
  String timeRange = '1h';

  @override
  Widget build(BuildContext context) {
    return Consumer<SystemStateProvider>(
      builder: (context, provider, child) {
        return Padding(
          padding: const EdgeInsets.all(16.0),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              _buildSelectionArea(),
              const SizedBox(height: 16),
              _buildTimeRangeSelector(),
              const SizedBox(height: 16),
              Expanded(
                child: _buildGraph(provider),
              ),
              const SizedBox(height: 16),
              _buildExportButtons(),
            ],
          ),
        );
      },
    );
  }

  Widget _buildSelectionArea() {
    return Row(
      children: [
        Expanded(
          child: _buildComponentSelector(),
        ),
        const SizedBox(width: 16),
        Expanded(
          child: _buildParameterSelector(),
        ),
      ],
    );
  }

  Widget _buildComponentSelector() {
    return DropdownButton<String>(
      value: selectedComponents.isNotEmpty ? selectedComponents.first : null,
      hint: const Text('Select Component'),
      isExpanded: true,
      onChanged: (String? newValue) {
        if (newValue != null) {
          setState(() {
            selectedComponents = [newValue];
            selectedParameters = [];
          });
        }
      },
      items: [
        'Nitrogen Generator',
        'Backline Heater',
        'Frontline Heater',
        'Precursor Heater 1',
        'Precursor Heater 2',
        'Reaction Chamber',
        'Valve 1',
        'Valve 2',
        'Pressure Control System',
        'Vacuum Pump',
      ].map<DropdownMenuItem<String>>((String value) {
        return DropdownMenuItem<String>(
          value: value,
          child: Text(value),
        );
      }).toList(),
    );
  }

  Widget _buildParameterSelector() {
    final parameters = _getParametersForComponent(selectedComponents.firstOrNull);
    return DropdownButton<String>(
      value: selectedParameters.isNotEmpty ? selectedParameters.first : null,
      hint: const Text('Select Parameter'),
      isExpanded: true,
      onChanged: (String? newValue) {
        if (newValue != null) {
          setState(() {
            selectedParameters = [newValue];
          });
        }
      },
      items: parameters.map<DropdownMenuItem<String>>((String value) {
        return DropdownMenuItem<String>(
          value: value,
          child: Text(value),
        );
      }).toList(),
    );
  }

  List<String> _getParametersForComponent(String? componentName) {
    switch (componentName) {
      case 'Nitrogen Generator':
        return ['flow_rate'];
      case 'Backline Heater':
      case 'Frontline Heater':
      case 'Precursor Heater 1':
      case 'Precursor Heater 2':
        return ['temperature'];
      case 'Reaction Chamber':
        return ['temperature', 'pressure'];
      case 'Valve 1':
      case 'Valve 2':
        return ['position'];
      case 'Pressure Control System':
        return ['pressure'];
      case 'Vacuum Pump':
        return ['power'];
      default:
        return [];
    }
  }

  Widget _buildTimeRangeSelector() {
    return SegmentedButton<String>(
      segments: const [
        ButtonSegment<String>(value: '1h', label: Text('1h')),
        ButtonSegment<String>(value: '24h', label: Text('24h')),
        ButtonSegment<String>(value: '7d', label: Text('7d')),
      ],
      selected: {timeRange},
      onSelectionChanged: (Set<String> newSelection) {
        setState(() {
          timeRange = newSelection.first;
        });
      },
    );
  }

  Widget _buildGraph(SystemStateProvider provider) {
    if (selectedComponents.isEmpty || selectedParameters.isEmpty) {
      return const Center(child: Text('Select a component and parameter to display the graph.'));
    }

    final component = provider.getComponentByName(selectedComponents.first);
    final parameter = selectedParameters.first;
    final data = component?.currentValues[parameter] ?? 0.0;

    // In a real app, we would fetch historical data based on the selected time range
    // For this example, we'll generate some mock data
    final List<FlSpot> spots = List.generate(100, (index) {
      return FlSpot(index.toDouble(), data + (index * 0.1));
    });

    return LineChart(
      LineChartData(
        gridData: FlGridData(show: true),
        titlesData: FlTitlesData(show: true),
        borderData: FlBorderData(show: true),
        lineBarsData: [
          LineChartBarData(
            spots: spots,
            isCurved: true,
            color: Colors.blue,
            dotData: FlDotData(show: false),
            belowBarData: BarAreaData(show: true),
          ),
        ],
      ),
    );
  }

  Widget _buildExportButtons() {
    return Row(
      mainAxisAlignment: MainAxisAlignment.end,
      children: [
        ElevatedButton(
          onPressed: _exportAsCSV,
          child: const Text('Export as CSV'),
        ),
        const SizedBox(width: 16),
        ElevatedButton(
          onPressed: _exportAsImage,
          child: const Text('Export as Image'),
        ),
      ],
    );
  }

  void _exportAsCSV() {
    // Implement CSV export functionality
    ScaffoldMessenger.of(context).showSnackBar(
      const SnackBar(content: Text('CSV export not implemented yet')),
    );
  }

  void _exportAsImage() {
    // Implement image export functionality
    ScaffoldMessenger.of(context).showSnackBar(
      const SnackBar(content: Text('Image export not implemented yet')),
    );
  }
}
