// troubleshooting_overlay.dart

import 'dart:convert';
import 'package:flutter/material.dart';
import 'package:provider/provider.dart';
import 'package:shared_preferences/shared_preferences.dart';
import '../providers/diagram_ald_system_provider.dart';

class TroubleshootingOverlay extends StatefulWidget {
  @override
  _TroubleshootingOverlayState createState() => _TroubleshootingOverlayState();
}

class _TroubleshootingOverlayState extends State<TroubleshootingOverlay> {
  Map<String, Offset> _componentPositions = {};
  Size _svgSize = Size.zero;

  @override
  void initState() {
    super.initState();
    _loadComponentPositions();
    WidgetsBinding.instance.addPostFrameCallback((_) {
      _updateSvgSize();
    });
  }

  void _updateSvgSize() {
    final RenderBox? renderBox = context.findRenderObject() as RenderBox?;
    if (renderBox != null) {
      setState(() {
        _svgSize = renderBox.size;
      });
    }
  }

  void _initializeDefaultPositions() {
    setState(() {
      _componentPositions = {
        'Nitrogen Generator': Offset(_svgSize.width * 0.10, _svgSize.height * 0.80),
        'MFC': Offset(_svgSize.width * 0.20, _svgSize.height * 0.70),
        'Backline Heater': Offset(_svgSize.width * 0.30, _svgSize.height * 0.60),
        'Frontline Heater': Offset(_svgSize.width * 0.40, _svgSize.height * 0.50),
        'Precursor Heater 1': Offset(_svgSize.width * 0.50, _svgSize.height * 0.40),
        'Precursor Heater 2': Offset(_svgSize.width * 0.60, _svgSize.height * 0.30),
        'Reaction Chamber': Offset(_svgSize.width * 0.70, _svgSize.height * 0.20),
        'Valve 1': Offset(_svgSize.width * 0.80, _svgSize.height * 0.10),
        'Valve 2': Offset(_svgSize.width * 0.90, _svgSize.height * 0.10),
        'Pressure Control System': Offset(_svgSize.width * 0.85, _svgSize.height * 0.85),
        'Vacuum Pump': Offset(_svgSize.width * 0.75, _svgSize.height * 0.75), // Ensure this line is included
      };
    });
  }


  Future<void> _loadComponentPositions() async {
    final prefs = await SharedPreferences.getInstance();
    final positionsJson = prefs.getString('component_positions_troubleshooting_overlay');
    if (positionsJson != null) {
      final positionsMap = jsonDecode(positionsJson) as Map<String, dynamic>;
      setState(() {
        _componentPositions = positionsMap.map((key, value) {
          final offsetList = (value as List<dynamic>).cast<double>();
          return MapEntry(key, Offset(offsetList[0], offsetList[1]));
        });
      });
    } else {
      // Initialize default positions if no saved positions are found
      _initializeDefaultPositions();
    }
  }

  Future<void> _saveComponentPositions() async {
    final prefs = await SharedPreferences.getInstance();
    final positionsMap = _componentPositions.map((key, value) {
      return MapEntry(key, [value.dx, value.dy]);
    });
    await prefs.setString('component_positions_troubleshooting_overlay', jsonEncode(positionsMap));
  }

  @override
  Widget build(BuildContext context) {
    return Consumer<SystemStateProvider>(
      builder: (context, systemStateProvider, child) {
        return Stack(
          children: _componentPositions.entries.map((entry) {
            final componentName = entry.key;
            final componentPosition = entry.value;

            // Get the component by name from the provider
            final component = systemStateProvider.getComponentByName(componentName);
if (component == null) return SizedBox.shrink();

            // Only display components that are activated
            if (!component.isActivated) {
              return SizedBox.shrink();
            }

            // Determine if the component has an error or warning status
            if (component.status == ComponentStatus.normal) {
              // No need to display normal components in troubleshooting overlay
              return SizedBox.shrink();
            }

            return Positioned(
              left: componentPosition.dx,
              top: componentPosition.dy,
              child: GestureDetector(
                onPanUpdate: (details) {
                  setState(() {
                    _componentPositions[componentName] = Offset(
                      componentPosition.dx + details.delta.dx,
                      componentPosition.dy + details.delta.dy,
                    );
                  });
                },
                onPanEnd: (_) {
                  _saveComponentPositions();
                },
                onTap: () => _showTroubleshootingDialog(context, component),
                child: Container(
                  width: 40, // Touch area size
                  height: 40,
                  alignment: Alignment.center,
                  child: Icon(
                    Icons.warning,
                    color: _getStatusColor(component.status),
                    size: 30,
                  ),
                ),
              ),
            );
          }).toList(),
        );
      },
    );
  }

  void _showTroubleshootingDialog(BuildContext context, SystemComponent component) {
    showDialog(
      context: context,
      builder: (context) => AlertDialog(
        title: Text('Troubleshoot ${component.name}'),
        content: SingleChildScrollView(
          child: Column(
            children: [
              Text('Status: ${component.status.toString().split('.').last}'),
              SizedBox(height: 10),
              if (component.errorMessages.isNotEmpty)
                ...component.errorMessages.map((message) => Text('- $message')).toList()
              else
                Text('No error messages.'),
            ],
          ),
        ),
        actions: [
          TextButton(
            onPressed: () {
              // Corrected: Pass component.name instead of component
              final systemStateProvider = Provider.of<SystemStateProvider>(context, listen: false);
              systemStateProvider.runDiagnostic(component.name);
              Navigator.of(context).pop();
            },
            child: Text('Run Diagnostic'),
          ),
          TextButton(
            onPressed: () => Navigator.of(context).pop(),
            child: Text('Close'),
          ),
        ],
      ),
    );
  }


  Color _getStatusColor(ComponentStatus status) {
    switch (status) {
      case ComponentStatus.warning:
        return Colors.yellow;
      case ComponentStatus.error:
        return Colors.red;
      default:
        return Colors.green; // Should not happen, but default to green
    }
  }
}
