// component_control_overlay.dart

import 'dart:convert';
import 'package:flutter/material.dart';
import 'package:provider/provider.dart';
import 'package:shared_preferences/shared_preferences.dart';
import '../models/system_component.dart';
import '../providers/ald_system_provider.dart';
import 'component_control_dialog.dart';

class ComponentControlOverlay extends StatefulWidget {
  final String overlayId;

  ComponentControlOverlay({required this.overlayId});

  @override
  _ComponentControlOverlayState createState() => _ComponentControlOverlayState();
}

class _ComponentControlOverlayState extends State<ComponentControlOverlay> {
  Map<String, Offset> _componentPositions = {};
  Size _diagramSize = Size.zero;

  @override
  void initState() {
    super.initState();
    _loadComponentPositions();
    WidgetsBinding.instance.addPostFrameCallback((_) {
      _updateDiagramSize();
    });
  }

  void _updateDiagramSize() {
    final RenderBox? renderBox = context.findRenderObject() as RenderBox?;
    if (renderBox != null) {
      setState(() {
        _diagramSize = renderBox.size;
      });
      // Initialize default positions after diagram size is known
      if (_componentPositions.isEmpty) {
        _initializeDefaultPositions();
      }
    }
  }

  Future<void> _resetComponentPositions() async {
    final prefs = await SharedPreferences.getInstance();
    await prefs.remove('component_positions_${widget.overlayId}');
    _initializeDefaultPositions();
    setState(() {}); // Refresh the UI
  }

  void _initializeDefaultPositions() {
    if (_diagramSize == Size.zero) return; // Diagram size not yet available

    setState(() {
      _componentPositions = {
        'Nitrogen Generator': Offset(_diagramSize.width * 0.05, _diagramSize.height * 0.50),
        'MFC': Offset(_diagramSize.width * 0.15, _diagramSize.height * 0.50),
        'Backline Heater': Offset(_diagramSize.width * 0.25, _diagramSize.height * 0.50),
        'Frontline Heater': Offset(_diagramSize.width * 0.35, _diagramSize.height * 0.50),
        'Precursor Heater 1': Offset(_diagramSize.width * 0.45, _diagramSize.height * 0.40),
        'Precursor Heater 2': Offset(_diagramSize.width * 0.45, _diagramSize.height * 0.60),
        'Reaction Chamber': Offset(_diagramSize.width * 0.60, _diagramSize.height * 0.50),
        'Valve 1': Offset(_diagramSize.width * 0.55, _diagramSize.height * 0.40),
        'Valve 2': Offset(_diagramSize.width * 0.55, _diagramSize.height * 0.60),
        'Pressure Control System': Offset(_diagramSize.width * 0.75, _diagramSize.height * 0.50),
        'Vacuum Pump': Offset(_diagramSize.width * 0.85, _diagramSize.height * 0.50),
      };
    });
  }

  Future<void> _loadComponentPositions() async {
    final prefs = await SharedPreferences.getInstance();
    final positionsJson = prefs.getString('component_positions_${widget.overlayId}');

    if (positionsJson != null) {
      final positionsMap = jsonDecode(positionsJson) as Map<String, dynamic>;
      setState(() {
        _componentPositions = positionsMap.map((key, value) {
          final offsetList = (value as List<dynamic>).cast<double>();
          return MapEntry(key, Offset(offsetList[0], offsetList[1]));
        });
      });
    } else {
      // If no saved positions, initialize default positions
      _initializeDefaultPositions();
    }
  }

  Future<void> _saveComponentPositions() async {
    final prefs = await SharedPreferences.getInstance();
    final positionsMap = _componentPositions.map((key, value) {
      return MapEntry(key, [value.dx, value.dy]);
    });
    await prefs.setString('component_positions_${widget.overlayId}', jsonEncode(positionsMap));
  }

  @override
  Widget build(BuildContext context) {
    return Consumer<SystemStateProvider>(
      builder: (context, systemStateProvider, child) {
        return GestureDetector(
          behavior: HitTestBehavior.translucent,
          onTapDown: (_) {
            // Dismiss any open dialogs or focus
            FocusScope.of(context).unfocus();
          },
          child: Stack(
            children: [
              ..._componentPositions.entries.map((entry) {
                final componentName = entry.key;
                final componentPosition = entry.value;

                // Get the component by name from the provider
                final component = systemStateProvider.getComponentByName(componentName);
if (component == null) return SizedBox.shrink();

                return Positioned(
                  left: componentPosition.dx,
                  top: componentPosition.dy,
                  child: GestureDetector(
                    behavior: HitTestBehavior.opaque,
                    onPanStart: (details) {
                      // Handle pan start if needed
                    },
                    onPanUpdate: (details) {
                      setState(() {
                        _componentPositions[componentName] = Offset(
                          _componentPositions[componentName]!.dx + details.delta.dx,
                          _componentPositions[componentName]!.dy + details.delta.dy,
                        );
                      });
                    },
                    onPanEnd: (_) {
                      _saveComponentPositions();
                    },
                    onTapUp: (details) {
                      _showComponentControlDialog(context, component);
                    },
                    child: Container(
                      width: 40, // Touch area size
                      height: 40,
                      alignment: Alignment.center,
                      child: Container(
                        width: 10, // Visual dot size
                        height: 10,
                        decoration: BoxDecoration(
                          color: component.isActivated ? _getStatusColor(component.status) : Colors.grey,
                          shape: BoxShape.circle,
                          border: Border.all(color: Colors.white, width: 1),
                        ),
                      ),
                    ),
                  ),
                );
              }).toList(),
              // Optional: Add a reset button for the overlay
              Positioned(
                top: 10,
                right: 10,
                child: IconButton(
                  icon: Icon(Icons.refresh, color: Colors.white),
                  onPressed: _resetComponentPositions,
                  tooltip: 'Reset Component Positions',
                ),
              ),
            ],
          ),
        );
      },
    );
  }

  void _showComponentControlDialog(BuildContext context, SystemComponent component) {
    showDialog(
      context: context,
      builder: (context) => ComponentControlDialog(component: component),
    );
  }

  Color _getStatusColor(ComponentStatus status) {
    switch (status) {
      case ComponentStatus.normal:
        return Colors.green;
      case ComponentStatus.warning:
        return Colors.yellow;
      case ComponentStatus.error:
        return Colors.red;
      default:
        return Colors.grey;
    }
  }
}
