// widgets/system_status_indicator.dart

import 'package:flutter/material.dart';
import 'package:provider/provider.dart';
import '../providers/experiment_planner_provider.dart';

class SystemStatusIndicator extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Consumer<SystemStateProvider>(
      builder: (context, provider, child) {
        String statusText = provider.isSystemRunning ? 'Running' : 'Stopped';
        Color statusColor = provider.isSystemRunning ? Colors.green : Colors.red;

        return Row(
          children: [
            Icon(Icons.circle, color: statusColor, size: 12),
            SizedBox(width: 4),
            Text(
              statusText,
              style: TextStyle(
                color: statusColor,
                fontWeight: FontWeight.bold,
              ),
            ),
          ],
        );
      },
    );
  }
}
